import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';

function Dashboard({ user, onLogout }) {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const fileInputRef = useRef(null);

  useEffect(() => {
    loadMessages();
    const interval = setInterval(loadMessages, 3000);
    return () => clearInterval(interval);
  }, []);

  const loadMessages = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('/api/messages', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMessages(response.data);
    } catch (error) {
      console.error('Failed to load messages:', error);
    }
  };

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!newMessage.trim()) return;

    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      await axios.post('/api/messages/text', 
        { message: newMessage },
        { headers: { Authorization: `Bearer ${token}` }}
      );
      setNewMessage('');
      loadMessages();
    } catch (error) {
      console.error('Failed to send message:', error);
    } finally {
      setLoading(false);
    }
  };

  const uploadFile = async (file) => {
    const formData = new FormData();
    formData.append('file', file);

    try {
      const token = localStorage.getItem('token');
      await axios.post('/api/messages/upload', formData, {
        headers: { 
          Authorization: `Bearer ${token}`,
          'Content-Type': 'multipart/form-data'
        }
      });
      loadMessages();
    } catch (error) {
      console.error('Failed to upload file:', error);
    }
  };

  const clearMessages = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete('/api/messages/clear', {
        headers: { Authorization: `Bearer ${token}` }
      });
      loadMessages();
    } catch (error) {
      console.error('Failed to clear messages:', error);
    }
  };

  const copyToClipboard = async (text, button) => {
    try {
      await navigator.clipboard.writeText(text);
      const originalText = button.textContent;
      button.textContent = 'âœ…Copied';
      setTimeout(() => {
        button.textContent = originalText;
      }, 1000);
    } catch (error) {
      console.error('Failed to copy:', error);
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    const file = e.dataTransfer.files[0];
    if (file) uploadFile(file);
  };

  const handleFileSelect = (e) => {
    const file = e.target.files[0];
    if (file) uploadFile(file);
  };

  const textMessages = messages.filter(msg => msg.type === 'text');
  const fileMessages = messages.filter(msg => msg.type === 'file');

  return (
    <div className="container">
      <div className="top-bar">
        <h1>Hello, {user.username}</h1>
        <button className="btn btn-danger" onClick={onLogout}>
          Log out
        </button>
      </div>

      <div className="card">
        <form onSubmit={sendMessage}>
          <input
            type="text"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            placeholder="Type a message..."
            required
          />
          <button className="btn" type="submit" disabled={loading}>
            Send
          </button>
        </form>

        <div 
          className="dropzone"
          onDragOver={(e) => e.preventDefault()}
          onDrop={handleDrop}
          onClick={() => fileInputRef.current?.click()}
        >
          ðŸ“Ž Drag & Drop file here or click to select
        </div>

        <input
          type="file"
          ref={fileInputRef}
          onChange={handleFileSelect}
          style={{ display: 'none' }}
          accept=".txt,.pdf,.png,.jpg,.jpeg,.docx"
        />
      </div>

      <div className="card">
        <button className="btn btn-danger" onClick={clearMessages}>
          ðŸ—‘ Clear All
        </button>
        <h2>Shared Content (Last 24h)</h2>

        <div className="content-container">
          <div className="text-column">
            {textMessages.map((msg, index) => (
              <div key={index} className="message">
                <span>{msg.content}</span>
                <button
                  onClick={(e) => copyToClipboard(msg.content, e.target)}
                  title="Copy"
                >
                  ðŸ“‹Copy
                </button>
              </div>
            ))}
          </div>

          <div className="file-column">
            {fileMessages.map((msg, index) => (
              <div key={index} className="file-item">
                <a
                  href={`/uploads/${msg.filename}`}
                  className="file-link"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  ðŸ“„ {msg.filename.split('/').slice(1).join('/')}
                </a>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
